/*****************************I. Palindrome *********************************/#include <stdio.h>//#include <cmath>//#include <limits.h>//#include <math.h>int main(void) {    unsigned int num = 0;    scanf("%d", &num);    unsigned int temp = 0, rev = 0, rem = 0;         temp = num;    do    {        rev *= 10;        rem = temp % 10;        temp /= 10;        rev += rem;    } while (temp);        if (num == rev)    {        printf("%d\n", rev);        printf("YES");    }    else    {        printf("%d\n", rev);        printf("NO");    }    return 0;  }